package jspiders;

public class AreaOfRect {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		float width =  4.5f;
		float height = 7.9f;
		
		float area = width * height ;
		
		System.out.println("Area :"+area);
		
		float peri = 2 * (width + height);
		
		System.out.println("Perimeter : "+peri);

	}

}
